             tmpId = 227811;
            // 関HC
            } else if (loginId == "sekihc"){
             tmpId = 227811;
            // 岐阜市HC
            } else if (loginId == "gifushihc"){
             tmpId = 227811;
            // 飛騨HC
            } else if (loginId == "hidahc"){
             tmpId = 227811;
            // 恵那HC
            } else if (loginId == "enahc"){
             tmpId = 466378;
            // 西濃HC
            } else if (loginId == "seinouhc"){
             tmpId = 466621;
            // テスト
            } else if (loginId == "tyouhyou"){
             tmpId = 465906;
            // Administrator
            } else if (loginId == "Administrator"){
             tmpId = 465906;
            // 上記以外
