// 日付カレンダーの書式変更
$(".datepicker:not(.applied)").each(function() {
 var n = $(this)
    , t = parseInt(n.attr("data-step"), 10);
 n.datetimepicker({
   format: n.attr("data-format"),
   timepicker: n.attr("data-timepicker") === "1",
   step: isNaN(t) ? 10 : t,
   dayOfWeekStart: 1,
   yearStart:1900,
   validateOnBlur:false,
   scrollInput:0,
   scrollMonth:1,
   scrollTime:1
 }).addClass("applied")
});

// 出力先：新規作成、編集
$p.events.after_validate = function (args) {
 let strKiou = $p.getControl('DescriptionJ').val();
 let age = "";
 let loginId = $p.loginId();

 // 1係以外の場合のみ、更新チェックを実施する
 if (!(loginId == "ichi" || loginId == "Administrator")) {
  // 疫学調査が完了の場合、氏名と第一報氏名のチェック 
  if ($p.getControl('ClassG').val() == "完了" && loginId == "gifushihc") {
   let nameChgFlg = document.getElementById('Results_CheckS').checked
   if ($p.getControl('DescriptionK').val() != $p.getControl('DescriptionA').val() && nameChgFlg == false) {
    document.getElementById('Results_CheckS').focus();
    alert("氏名が1報-氏名と異なります。\n異なっていることが問題ない場合は 《氏名が一報と異なることをチェックしました。》 にチェックを入れてください。" );
    return false;
   } 
  }

  // 生年月日と年齢のチェック（警告）
  if ($p.getControl('DateB').val() != "" && $p.getControl('DateA').val() != "") {
   age = getAgeCalc($p.getControl('DateB').val(), $p.getControl('DateA').val());
  }
  if (age != $p.getControl('NumA').val()) {
   document.getElementById('Results_NumA').focus();
   alert("[診断日]と[生年月日]から算出した年齢 と [年齢]欄の値に差異があります。\n念のため、確認してください。" );
  }

  // 既往歴の入力チェック（警告）
  if (strKiou.indexOf("血圧") >= 0) {
   alert("高血圧であれば、疾患詳細でチェックができます。必要あれば、修正してください。");
  }

  // リスクが 低 の場合、入力チェックを行う
  if ($p.getControl('Class192').val() == "低") {
   let msgInfo = isCheckLowRisk();
   if (msgInfo != "") {
    alert(msgInfo);
    return false;
   }
  }

  // 疫学調査が 完了 の場合、入力チェックを行う
  if ($p.getControl('ClassG').val() == "完了") {
   if (document.getElementById('EditCheckFlag').checked && document.getElementById('EditInfoText').value == "") {
    alert("完了後の修正時には、修正内容を必ず記入して下さい。");
    document.getElementById('EditInfoText').focus();
    return false;
   }

   if ($p.getControl('Class192').val() == "低") {
    let checkRes = isCheckBeforeUpdate("低");
    if (checkRes != "") {
     alert("以下の項目は必ず入力してください。\n" + checkRes );
     return false;
    }
   } else {
    let checkRes = isCheckBeforeUpdate("高");
    if (checkRes != "") {
     alert("以下の項目は必ず入力してください。\n" + checkRes );
     return false;
    }
   }
  }
 }
}

