 setNendai();

 // 隠し項目への値設定
 // 入院の更新
 setHospitalHiden();
 // 宿泊療養の更新
 setStayHiden();
 // 自宅療養の更新
 setHomeHiden();
 // 最新状況の更新
 let strLastStatus = getLastStatus();
 // 経過の更新
 let strStatus = "";
 strStatus = statusQuoUpdate("Results_ClassL","ClassL",strStatus);
 strStatus = statusQuoUpdate("Results_ClassE","ClassE",strStatus);
 strStatus = statusQuoUpdate("Results_ClassJ","ClassJ",strStatus);
 strStatus = statusQuoUpdate("Results_ClassM","ClassM",strStatus);
 strStatus = statusQuoUpdate("Results_Class031","Class031",strStatus);
 strStatus = statusQuoUpdate("Results_Class032","Class032",strStatus);
 strStatus = statusQuoUpdate("Results_Class038","Class038",strStatus);
 strStatus = statusQuoUpdate("Results_Class034","Class034",strStatus);
 strStatus = statusQuoUpdate("Results_Class035","Class035",strStatus);
 strStatus = statusQuoUpdate("Results_Class036","Class036",strStatus);
 strStatus = statusQuoUpdate("Results_Class102","Class102",strStatus);
 strStatus = statusQuoUpdate("Results_Class103","Class103",strStatus);
 strStatus = statusQuoUpdate("Results_Class104","Class104",strStatus);
 strStatus = statusQuoUpdate("Results_Class105","Class105",strStatus);
 strStatus = statusQuoUpdate("Results_Class106","Class106",strStatus);
 // ドロップダウンより取得した値を設定する（経過、最新状況）
 $p.set($p.getControl('Description056'), strStatus);
 $p.set($p.getControl('Class107'), strLastStatus);
 // ステータスを判定し、隠し項目に日付を設定する
 let nowDate = new Date();
 if (strLastStatus == "入院待") {
  $p.set($p.getControl('Date063'), $.datepicker.formatDate("yy/mm/dd",nowDate));
  $p.set($p.getControl('Date064'), '2100/01/01');
