  $p.set($p.getControl('Date064'), $.datepicker.formatDate("yy/mm/dd",nowDate));
  $p.set($p.getControl('Date065'), '2100/01/01');
 } else if (strLastStatus == "自宅E済") {
  $p.set($p.getControl('Date063'), '2100/01/01');
  $p.set($p.getControl('Date064'), '2100/01/01');
  $p.set($p.getControl('Date065'), $.datepicker.formatDate("yy/mm/dd",nowDate));
 } else {
  $p.set($p.getControl('Date063'), '2100/01/01');
  $p.set($p.getControl('Date064'), '2100/01/01');
  $p.set($p.getControl('Date065'), '2100/01/01');
 }

 // 印刷用タイトル
