$p.on('change','ClassW',function(){
 let symptoms = $p.getControl('ClassW').val();
 let eDateC = document.getElementById('Results_DateC');

 // なし を選択した場合
 if (symptoms == "なし") {
  // 発症日のクリア、および、非活性とする。
  $p.set($p.getControl('DateC') , "");
  eDateC.disabled = true;
  eDateC.style.backgroundColor = "#dcdcdc";

  if ($p.getControl('DateC').val() == "" && $p.getControl('DateY').val() != "") {
   let tempdate = new Date($p.getControl('DateY').val());
   tempdate.setDate(tempdate.getDate() + 7);
   // 日付の表示形式を変更
   $p.set($p.getControl('DateD'),$.datepicker.formatDate("mm/dd",tempdate));

   $p.set($p.getControl('Date025'), $.datepicker.formatDate("mm/dd",new Date($p.getControl('DateY').val())));
   $p.set($p.getControl('DateF'), $.datepicker.formatDate("mm/dd",new Date($p.getControl('DateY').val())));
  }

  symptomaticOnsetDate();
  behavioralOnsetDate();
 } else {
  eDateC.disabled = false;
  eDateC.style.backgroundColor = "#ffffff";
 }

})
