function prevCopy(btemperature, bspo2, bsymptoms, bdetail, ntemperature, nspo2, nsymptoms, ndetail){
 $p.set($p.getControl(ntemperature), $p.getControl(btemperature).val());
 $p.set($p.getControl(nspo2), $p.getControl(bspo2).val());
 $p.set($p.getControl(ndetail), $p.getControl(bdetail).val());

 // ドロップダウンのコピー
 let select_list = $p.getControl(bsymptoms).val();
 let opts = "";
 let opts2 = "";
 for (let i = 0; i < select_list.length; i++) {
  if ( i == 0) {
   opts = "\"" + select_list[i] + "\"";
   opts2 = select_list[i]
  } else {
   opts = opts + ", \"" + select_list[i] + "\"";
   opts2 = opts2 + ", " + select_list[i] 
  }
 }
 opts = "[" + opts + "]";
 $p.set($p.getControl(nsymptoms), opts);

 let s1 = document.getElementById("Results_" + nsymptoms + "_ms");
 s1.style="width:100%";
 //s1.innerHTML = "<span class='ui-multiselect-open' title='Open'><span class='ui-icon ui-icon-triangle-1-s'></span></span><span>" + opts2 + "</span>"
 //s1.innerHTML = "<span class='ui-multiselect-open' title='Open'><span class='ui-icon ui-icon-triangle-1-s'></span></span><span>" + $p.getControl(bsymptoms).val(); + "</span>"
}

