// 付添タブへの値設定
// ------------------------------------------------------------------------------------
function setTukisoi(name, kana, gender, birthday, age, relation, address) {
 // 連絡先の取得
 let tel = "";
 if ($p.getControl('Description019').val() == $p.getControl(name).val()) {
  tel = $p.getControl('Description064').val();
 } else if ($p.getControl('Description022').val() == $p.getControl(name).val()) {
  tel = $p.getControl('Description065').val();
 }
 // 住所の取得
 //let address = "";
 //if ($p.getControl('Description026').val() != "") {
 // address = $p.getControl('Description026').val()
 //} else {
 // address = $p.getControl('Class138').val() + $p.getControl('Class139').val() + $p.getControl('DescriptionH').val() + $p.getControl('DescriptionI').val() 
 //}

 // 付添者1が未入力であるか、または、引数と同じ名前であるか
 if ($p.getControl('Description114').val() == "" || $p.getControl('Description114').val() == $p.getControl(name).val()) {
  $p.set($p.getControl('Description114'), $p.getControl(name).val());
  $p.set($p.getControl('Description115'), $p.getControl(kana).val());
  $p.set($p.getControl('Class121'), $p.getControl(gender).val());
  $p.set($p.getControl('Date080'), $p.getControl(birthday).val());
  $p.set($p.getControl('Num023'), $p.getControl(age).val());
  $p.set($p.getControl('Description113'), tel);
  $p.set($p.getControl('Description112'), $p.getControl(address).val());
  $p.set($p.getControl('Class004'), $p.getControl(relation).val());
 } else if ($p.getControl('Description119').val() == "" || $p.getControl('Description119').val() == $p.getControl(name).val()) {
  $p.set($p.getControl('Description119'), $p.getControl(name).val());
  $p.set($p.getControl('Description120'), $p.getControl(kana).val());
  $p.set($p.getControl('Class150'), $p.getControl(gender).val());
  $p.set($p.getControl('Date102'), $p.getControl(birthday).val());
  $p.set($p.getControl('Num025'), $p.getControl(age).val());
  $p.set($p.getControl('Description121'), tel);
  $p.set($p.getControl('Description122'), $p.getControl(address).val());
  $p.set($p.getControl('Class005'), $p.getControl(relation).val());
 } else if ($p.getControl('Description126').val() == "" || $p.getControl('Description126').val() == $p.getControl(name).val()) {
  $p.set($p.getControl('Description126'), $p.getControl(name).val());
  $p.set($p.getControl('Description127'), $p.getControl(kana).val());
  $p.set($p.getControl('Class156'), $p.getControl(gender).val());
  $p.set($p.getControl('Date103'), $p.getControl(birthday).val());
  $p.set($p.getControl('Num026'), $p.getControl(age).val());
  $p.set($p.getControl('Description128'), tel);
  $p.set($p.getControl('Description129'), $p.getControl(address).val());
  $p.set($p.getControl('Class006'), $p.getControl(relation).val());
 } else {
  if (!window.confirm('付添者1～3にはすでに別の方が登録されています。\n付添者3に上書きしてもよろしいでしょうか？')) { return; } 
  $p.set($p.getControl('Description126'), $p.getControl(name).val());
  $p.set($p.getControl('Description127'), $p.getControl(kana).val());
  $p.set($p.getControl('Class156'), $p.getControl(gender).val());
  $p.set($p.getControl('Date103'), $p.getControl(birthday).val());
  $p.set($p.getControl('Num026'), $p.getControl(age).val());
  $p.set($p.getControl('Description128'), tel);
  $p.set($p.getControl('Description129'), $p.getControl(address).val());
  $p.set($p.getControl('Class006'), $p.getControl(relation).val());
 }
}

