// ----------------------------------------------------------------------------
// 疫学調査完了後の入力制御
// ----------------------------------------------------------------------------
function compInputControl() {
 let state = $p.getControl('ClassG').val();
 let loginId = $p.loginId();
 // 疫学調査が完了でない場合、処理を終了
 if (state != "完了") { return; }
 // ログインIDが保健所でない場合、処理を終了
 if ( !(loginId == "enahc" || loginId == "hidahc" || loginId == "seinouhc" || loginId == "gifuhc" || loginId == "sekihc" || loginId == "tounouhc" || loginId == "kamohc" || loginId == "gifushihc")) { return; }

  // 各項目をロックする
  editLockOnOff(true, "#dcdcdc");

  // ファーストコール済の場合、エントリーボタンを非活性
  if ($p.getControl('Class108').val() == "ファーストコール済") {
    document.getElementById('Button_fc').disabled = true;
    document.getElementById('Button_stay').disabled = true;
    document.getElementById('Button_stayCancel').disabled = true;
    document.getElementById('Button_home').disabled = true;
    document.getElementById('Button_homeCancel').disabled = true;
  }
  // 宿泊療養施設エントリー済以外の場合、宿泊取消ボタンを非活性
  // 宿泊療養施設エントリー済の場合、宿泊取消ボタン以外を非活性
  if ($p.getControl('Class108').val() == "宿泊療養施設エントリー済") {
    document.getElementById('Button_stayCancel').disabled = false;
    document.getElementById('Button_fc').disabled = true;
    document.getElementById('Button_home').disabled = true;
    document.getElementById('Button_homeCancel').disabled = true;
  } else {
    document.getElementById('Button_stayCancel').disabled = true;
    document.getElementById('Button_fc').disabled = false;
    document.getElementById('Button_home').disabled = false;
    document.getElementById('Button_homeCancel').disabled = false;
  }
  // 自宅療養（県庁）エントリー済以外の場合、自宅取消ボタンを非活性
  if ($p.getControl('Class108').val() == "自宅療養（県庁）エントリー済") {
    document.getElementById('Button_homeCancel').disabled = false;
  } else {
    document.getElementById('Button_homeCancel').disabled = true;
  }

}
