// ----------------------------------------------------------------------------
// 年齢の計算
// ----------------------------------------------------------------------------
function ageCalc(eDate1,eDate2,eNum1) {
 let age = 0;

 // 生年月日と診断日に値が入っている場合のみ処理をおこなう
 if ($p.getControl(eDate1).val() != "" && $p.getControl(eDate2).val() != "") {
  // 年齢計算
  let birthDate = new Date($p.getControl(eDate1).val());
  let diagnosisDate = new Date($p.getControl(eDate2).val());
  age = diagnosisDate.getFullYear() - birthDate.getFullYear();

  // 0歳でない場合、診断日が生年月日を迎えていなければ減算
  // 0歳の場合、診断日が生年月日を迎えていれば加算 
  let currentBirthDate = new Date(diagnosisDate.getFullYear(), birthDate.getMonth(), birthDate.getDate());
  if(age != 0) {
    if(diagnosisDate < currentBirthDate) { age--; }
  //} else {
  //  if(diagnosisDate >= currentBirthDate) { age++; }
  }

  // 計算結果を設定する
  $p.set($p.getControl(eNum1), age);
 }
}

