// ---------------------------------------------------------------------------
// 入力文字の制限
// ---------------------------------------------------------------------------
// 入力文字チェック
function harfCheck(temp) {
 let str = temp.slice(-1);
 if (!str.match(/^[0-9]*$/)) {
  if (str.match(/^[０-９]*$/)) {
   str = replaceFullToHalf(str);
   temp = temp.slice(0,temp.length -1) + str;
  } else {
   temp = temp.slice(0,temp.length -1);
  }
 }
 return temp;
}
// 入力文字チェック２
function harfCheck2(temp) {
 let length = temp.length
 let str = "";
 let result = "";
 for (var i = 0; i<length; i++) {
  str = temp.substr(i,1);
  if (!str.match(/^[0-9]*$/)) {
   if (str.match(/^[０-９]*$/)) {
    str = replaceFullToHalf(str);
   } else {
    str = "";
   }
  }
  result = result + str;
 }
 return result;
}
// 入力文字チェック３（小数点可）
function harfCheck3(temp) {
 let length = temp.length
 let str = "";
 let result = "";
 for (var i = 0; i<length; i++) {
  str = temp.substr(i,1);
  if (!str.match(/^[0-9.]*$/)) {
   if (str.match(/^[０-９]*$/)) {
    str = replaceFullToHalf(str);
   } else {
    str = "";
   }
  }
  result = result + str;
 }
 return result;
}

// 全角→半角(英数字)
function replaceFullToHalf(str){
  return str.replace(/[！-～]/g, function(s){
    return String.fromCharCode(s.charCodeAt(0) - 0xFEE0);
  });
}
