// ----------------------------------------------------------------------------
// BMIの計算を行う
// ----------------------------------------------------------------------------
function bmiCalc(hCol,wCol,setCol) {
 let eId = "Results_" + setCol
 let height = $p.getControl(hCol).val();
 let weight = $p.getControl(wCol).val();
 // 身長(NumE)と体重(NumF)に値が入っているか
 if(height !="" && weight  != ""){

  // BMI計算
  let bmi = weight / (height/100) / (height/100) ;
  bmi = Math.round(bmi * 100) / 100 ;
  // BMI項目に値を設定
  $p.set($p.getControl(setCol),bmi);
 }
}
