function getMailCc() {
 let loginId = $p.loginId();
 let ccMail = "";

 switch(loginId) {
  case "gifuhc":
   ccMail = gifuHcCc;
   break;
  case "seinouhc":
   ccMail = seinouHcCc;
   break;
  case "sekihc":
   ccMail  = sekiHcCc;
   break;
  case "kamohc":
   ccMail = kamoHcCc;
   break;
  case "tounouhc":
   ccMail = tounouHcCc;
   break;
  case "enahc":
   ccMail = enaHcCc;
   break;
  case "hidahc":
   ccMail = hidaHcCc;
   break;
  case "gifushihc":
  ccMail = gifushiHcCc;
  break;
 case "jitaku":
  ccMail = jitakuCc;
  break;
 case "hotel":
  ccMail = hotelCc;
  break;
 default:
  ccMail = suisinkaCc;
  break;
 }
 return ccMail;
}
