// 和暦-西暦変換
function ChangeSeireki(temp) {
 let year = temp.split("/")[0].substr(1);
 let month = temp.split("/")[1];
 let day = temp.split("/")[2];
 let result = "";
 if (ckDate(temp) == false) {
  if (temp.substr(0,1) == "M" || temp.substr(0,1) == "m" ) {
   if (isNaN(year) == false) { year = 1867 + parseInt(year) }
  } else if (temp.substr(0,1) == "T" || temp.substr(0,1) == "t" ) {
   if (isNaN(year) == false) { year = 1911 + parseInt(year) }
  } else if (temp.substr(0,1) == "S" || temp.substr(0,1) == "s") {
   if (isNaN(year) == false) { year = 1925 + parseInt(year) }
  } else if (temp.substr(0,1) == "H" || temp.substr(0,1) == "h") {
   if (isNaN(year) == false) { year = 1988 + parseInt(year) }
  } else if (temp.substr(0,1) == "R" || temp.substr(0,1) == "r") {
   if (isNaN(year) == false) { year = 2018 + parseInt(year) }
  } else {
   return result;
  }
  result = year + "/" + month + "/" + day;
 }
  return result;
}
function ckDate(strDate) {
