// リスク：低の入力チェック
// ----------------------------------------------------------------------------
function isCheckLowRisk() {
 let msg = "";
 let disease = "";
 let i = 0;
 // 変数に疾患詳細を設定
 if ($p.getControl('ClassF').val() != "") {
  disease = $p.getControl('ClassF').val();
  // 疾患詳細に"なし"が選択されている場合、削除する
  for (i = 0; i < disease.length; i++) {
   if (disease[i] == "なし" || disease[i] == "肝疾患") {
    disease.splice(i,1);
    break;
   }
  }
 }

 // 年齢の判定
 if ($p.getControl('NumA').val() >= 65 || $p.getControl('NumA').val() == "") {
  msg = "年齢が65歳以上、または、未入力のため、リスク：低 は選択できません。";
 }
 // 妊娠の判定
 if ($p.getControl('ClassZ').val() == "あり" || ($p.getControl('ClassZ').val() == "" && $p.getControl('ClassA').val() == "女性")) {
  msg = "妊娠が \"あり\" または、未入力のため、リスク：低 は選択できません。";
 }

 // 40～64歳
 if ($p.getControl('NumA').val() >= 40 && $p.getControl('NumA').val() <= 64) {
  // 疾患が2つ以上の判定
  if (disease.length >= 2) {
   msg = "年齢が40歳～64歳、かつ、疾患が2つ以上であるため、リスク：低 は選択できません。";
  }

  let riskCnt = 0;
  // 疾患が1つの判定
  if (disease.length == 1) { riskCnt = riskCnt + 1; }
  // BMIが30以上の判定
  if ($p.getControl('NumG').val() >= 30 || $p.getControl('NumG').val() == "") { riskCnt = riskCnt + 1; }
  // 喫煙がありの判定
  if ($p.getControl('ClassY').val() == "あり" || $p.getControl('ClassY').val() == "") { riskCnt = riskCnt + 1; }

  if (riskCnt >= 2) {
   msg = "年齢が40歳～64歳、かつ、以下のいずれか２つを満たすため、リスク：低 は選択できません。\n・疾患詳細を1つ選択\n・BMIが30以上、または、未入力\n・喫煙があり、または、未入力";
  }
 }

 return msg;
}
// ----------------------------------------------------------------------
// 日付の大小判定
// ----------------------------------------------------------------------
function lowerThanDateOnly(date1, date2) {
 var year1 = date1.getFullYear();
 var month1 = date1.getMonth() + 1;
 var day1 = date1.getDate();

 var year2 = date2.getFullYear();
 var month2= date2.getMonth() + 1;
 var day2 = date2.getDate();

 if (year1 == year2) {
  if (month1 == month2) { 
   return day1 < day2;
  } else { 
   return month1 < month2;
  }
 } else {
  return year1 < year2; 
 }
}
// ----------------------------------------------------------------------------
// 年齢の計算
// ----------------------------------------------------------------------------
function getAgeCalc(eDate1,eDate2) {
 let age = 0;

 // 年齢計算
 let birthDate = new Date(eDate1);
 let diagnosisDate = new Date(eDate2);
 age = diagnosisDate.getFullYear() - birthDate.getFullYear();

 // 0歳でない場合、診断日が生年月日を迎えていなければ減算
 let currentBirthDate = new Date(diagnosisDate.getFullYear(), birthDate.getMonth(), birthDate.getDate());
 if(age != 0) {
   if(diagnosisDate < currentBirthDate) { age--; }
 }
 return age;
}
