// ----------------------------------------------------------------------------
// 年齢の計算
// ----------------------------------------------------------------------------
function ageCalc(eDate1,eDate2) {
 let age = 0;

 // 年齢計算
 let birthDate = new Date(eDate1);
 let diagnosisDate = new Date(eDate2);
 age = diagnosisDate.getFullYear() - birthDate.getFullYear();

 // 0歳でない場合、診断日が生年月日を迎えていなければ減算
 let currentBirthDate = new Date(diagnosisDate.getFullYear(), birthDate.getMonth(), birthDate.getDate());
 if(age != 0) {
   if(diagnosisDate < currentBirthDate) { age--; }
 }
 return age;
}
// ----------------------------------------------------------------------------
// 自宅ｴﾝﾄﾘｰ日時(FC)を取得
// ----------------------------------------------------------------------------
function getEntryDateFC() {
 // 自宅ｴﾝﾄﾘｰ日時(FC)の設定
 let entryDate = "";
 let now = new Date();
 let month = now.getMonth()+1
 let date = now.getDate();

 if (now.getHours() >= 0 && now.getHours() < 9) {
  entryDate = month + "月" + date + "日　9時" ;
 } else if (now.getHours() >= 9 && now.getHours() < 17) {
  if (now.getHours() == 9 && now.getMinutes() <= 0) {
   entryDate = month + "月" + date + "日　9時" ;
  } else {
   now.setDate(now.getDate() + 1);
   date = now.getDate();
   if (date == 1) { 
    month = month + 1;
   }
   entryDate = month + "月" + date + "日　9時" ;
  }
 } else {
  now.setDate(now.getDate() + 1);
  date = now.getDate();
  if (date == 1) { 
   month = month + 1;
  }
  entryDate = month + "月" + date + "日　9時" ;
 }

 return entryDate;
}

