// ----------------------------------------------------------------------------
// BMI
// ----------------------------------------------------------------------------
function getBmi2() {
 let bmi = "";
 let height = model.NumE;
 let weight = model.NumF;

 // 身長と体重に値が入っているか
 if (height != "" && weight != "") {
  bmi = weight / (height/100) / (height/100);
  bmi = Math.round(bmi * 100) / 100;
 }

 return bmi;
}
// ----------------------------------------------------------------------------
// 日付の取得
// ----------------------------------------------------------------------------
function getCalcDate2(eDate, count, flg) {
 let temp = new Date(eDate);
 if (flg == "P") {
  temp.setDate(temp.getDate() + count);
 } else if (flg == "M") {
  temp.setDate(temp.getDate() - count);
 }
 return temp;
}
